// Validate tenant and prepare jewelry-specific price update
const data = $input.first().json || {};
const tenantId = $env.TENANT_ID;

if (!tenantId) {
  throw new Error('TENANT_ID environment variable not set');
}

// Jewelry-specific configuration
const jewelryConfig = {
  tenant_id: tenantId,
  business_type: 'jewelry',
  price_sources: ['gold_api', 'silver_api', 'platinum_api'],
  metal_types: ['gold', 'silver', 'platinum'],
  karat_levels: ['10', '14', '18', '22', '24'],
  force_update: data.force_update || false,
  update_thresholds: {
    minimum_change_percent: 1.5,
    significant_change_percent: 5.0
  },
  platforms: ['odoo', 'woocommerce', 'shopify']
};

return { json: jewelryConfig };

// Calculate jewelry-specific prices with precious metals logic
const metalsData = $('get-precious-metals-prices').first().json;
const exchangeData = $('get-exchange-rates').first().json;
const pricingRules = $('get-jewelry-pricing-rules').first().json;
const jewelryProducts = $('get-jewelry-products').first().json.data || [];
const config = $('prepare-jewelry-config').first().json;

// Extract market prices
const goldPriceUSD = metalsData.gold?.price || 2650;
const silverPriceUSD = metalsData.silver?.price || 32;
const platinumPriceUSD = metalsData.platinum?.price || 1200;
const exchangeRate = exchangeData.rates?.BRL || 5.2;

// Convert to BRL per gram
const goldPriceBRLPerGram = (goldPriceUSD * exchangeRate) / 31.1035;
const silverPriceBRLPerGram = (silverPriceUSD * exchangeRate) / 31.1035;
const platinumPriceBRLPerGram = (platinumPriceUSD * exchangeRate) / 31.1035;

// Jewelry-specific pricing logic
const karatPurities = {
  '24': 1.0,
  '22': 0.916,
  '18': 0.75,
  '14': 0.585,
  '10': 0.417
};

const metalPricesPerGram = {
  'gold': goldPriceBRLPerGram,
  'silver': silverPriceBRLPerGram,
  'platinum': platinumPriceBRLPerGram
};

// Get pricing rules from AWS Secrets
const markupRules = pricingRules.markup_rules || {
  '24': 2.8, '22': 2.6, '18': 2.5, '14': 2.3, '10': 2.1
};

const laborCosts = pricingRules.labor_costs || {
  '24': 35, '22': 30, '18': 25, '14': 20, '10': 15
};

const productsToUpdate = [];

// Calculate new prices for each jewelry product
for (const product of jewelryProducts) {
  const metalType = product.x_metal_type;
  const karat = product.x_metal_karat || '18';
  const weight = parseFloat(product.x_weight) || 1;

  if (!metalType || !metalPricesPerGram[metalType]) {
    continue; // Skip products without metal type
  }

  const basePricePerGram = metalPricesPerGram[metalType];
  const purity = karatPurities[karat] || 0.75;
  const markup = markupRules[karat] || 2.5;
  const laborCost = laborCosts[karat] || 25;

  // Calculate material cost
  const materialCost = basePricePerGram * purity * weight;

  // Add labor cost (per gram)
  const totalLaborCost = laborCost * weight;

  // Apply markup
  const newPrice = (materialCost + totalLaborCost) * markup;

  // Round to 2 decimal places
  const finalPrice = Math.round(newPrice * 100) / 100;

  // Check if price change meets threshold
  const oldPrice = product.list_price || 0;
  const priceChangePercent = oldPrice > 0 ? ((finalPrice - oldPrice) / oldPrice) * 100 : 100;

  const shouldUpdate = config.force_update || 
                      Math.abs(priceChangePercent) >= config.update_thresholds.minimum_change_percent;

  if (shouldUpdate) {
    productsToUpdate.push({
      odoo_id: product.id,
      sku: product.default_code,
      name: product.name,
      metal_type: metalType,
      karat: karat,
      weight: weight,
      old_price: oldPrice,
      new_price: finalPrice,
      price_change: finalPrice - oldPrice,
      price_change_percent: priceChangePercent,
      material_cost: Math.round(materialCost * 100) / 100,
      labor_cost: totalLaborCost,
      markup_multiplier: markup,
      market_data: {
        base_price_per_gram: Math.round(basePricePerGram * 100) / 100,
        purity: purity,
        exchange_rate: exchangeRate
      }
    });
  }
}

// Categorize updates by significance
const significantUpdates = productsToUpdate.filter(p => 
  Math.abs(p.price_change_percent) >= config.update_thresholds.significant_change_percent
);

const minorUpdates = productsToUpdate.filter(p => 
  Math.abs(p.price_change_percent) < config.update_thresholds.significant_change_percent
);

return {
  json: {
    tenant_id: config.tenant_id,
    business_type: 'jewelry',
    timestamp: new Date().toISOString(),
    market_data: {
      gold_price_usd: goldPriceUSD,
      silver_price_usd: silverPriceUSD,
      platinum_price_usd: platinumPriceUSD,
      exchange_rate_brl: exchangeRate,
      gold_price_brl_per_gram: Math.round(goldPriceBRLPerGram * 100) / 100,
      silver_price_brl_per_gram: Math.round(silverPriceBRLPerGram * 100) / 100,
      platinum_price_brl_per_gram: Math.round(platinumPriceBRLPerGram * 100) / 100
    },
    products_to_update: productsToUpdate,
    update_summary: {
      total_products_checked: jewelryProducts.length,
      products_to_update: productsToUpdate.length,
      significant_updates: significantUpdates.length,
      minor_updates: minorUpdates.length,
      force_update: config.force_update
    }
  }
};

