// Analyze product URL and determine optimal scraping strategy
const product = $json.products || {};
const url = product.url || '';

if (!url) {
  return {
    error: 'Product URL is required',
    success: false
  };
}

let domain = '';
try {
  domain = new URL(url).hostname;
} catch (error) {
  return {
    error: 'Invalid URL format',
    success: false
  };
}

// Define strategy rules based on website characteristics
const strategyRules = {
  // AI-first strategy for complex e-commerce sites
  'ai_first': [
    'amazon.com', 'amazon.com.br', 'mercadolivre.com.br', 'alibaba.com',
    'shopify.com', 'woocommerce.com', 'magento.com'
  ],

  // Scrapy-first for simpler, consistent sites
  'scrapy_first': [
    'americanas.com.br', 'submarino.com.br', 'casasbahia.com.br',
    'extra.com.br', 'pontofrio.com.br'
  ],

  // Hybrid approach for medium complexity
  'hybrid': [
    'ebay.com', 'etsy.com', 'walmart.com', 'target.com'
  ]
};

// Determine strategy safely
let strategy = 'ai_first'; // Default to AI-first
const strategyEntries = Object.entries(strategyRules);
for (const [strategyType, domains] of strategyEntries) {
  if (Array.isArray(domains) && domains.some(d => domain.includes(d))) {
    strategy = strategyType;
    break;
  }
}

// Generate extraction configuration based on product type
const extractionConfig = {
  title: product.title_selector || 'h1, .product-title, [data-testid="product-title"]',
  price: product.price_selector || '.price, .product-price, [data-testid="price"], .a-price-whole',
  availability: product.availability_selector || '.availability, .stock-status, [data-testid="availability"]',
  description: product.description_selector || '.description, .product-description',
  images: product.images_selector || 'img[src*="product"], .product-image img',
  rating: product.rating_selector || '.rating, .stars, [data-testid="rating"]'
};

return {
  product_id: product.id || `product_${Date.now()}`,
  product_name: product.name,
  product_url: url,
  domain: domain,
  scraping_strategy: strategy,
  extraction_config: extractionConfig,
  tenant_id: $('Extract Parameters').item.json.tenant_id,
  timestamp: new Date().toISOString()
};

