// Process Crawl4AI response for price data
const response = $input.all()[0].json;
const productData = $('Analyze Product Strategy').item.json;

if (response.success && response.results && response.results.length > 0) {
  const result = response.results[0];
  const extractedData = result.extracted_content;

  // Parse the structured data
  let productInfo = {};
  try {
    productInfo = typeof extractedData === 'string' ? JSON.parse(extractedData) : extractedData;
  } catch (error) {
    console.log('Error parsing extracted data:', error);
    productInfo = {};
  }

  return {
    success: true,
    product_id: productData.product_id,
    product_name: productData.product_name,
    url: productData.product_url,
    title: productInfo.title || '',
    current_price: productInfo.current_price || 0,
    original_price: productInfo.original_price || productInfo.current_price || 0,
    currency: productInfo.currency || 'BRL',
    availability: productInfo.availability || 'unknown',
    description: productInfo.description || '',
    discount_percentage: productInfo.discount_percentage || 0,
    rating: productInfo.rating || 0,
    extraction_method: 'crawl4ai_ai',
    tenant_id: productData.tenant_id,
    timestamp: new Date().toISOString()
  };
} else {
  return {
    success: false,
    product_id: productData.product_id,
    url: productData.product_url,
    error: response.error || 'Failed to extract price data',
    extraction_method: 'crawl4ai_ai',
    tenant_id: productData.tenant_id,
    timestamp: new Date().toISOString()
  };
}

// Process Scrapy response with manual parsing
const cheerio = require('cheerio');
const response = $input.all()[0];
const productData = $('Analyze Product Strategy').item.json;
const config = productData.extraction_config;

if (response.statusCode === 200) {
  const html = response.body;
  const $ = cheerio.load(html);

  // Extract title
  const title = $(config.title).first().text().trim() || $('h1').first().text().trim();

  // Extract price with multiple strategies
  let currentPrice = 0;
  let originalPrice = 0;
  let currency = 'BRL';

  // Try configured price selector first
  let priceText = $(config.price).first().text().trim();

  // Fallback price selectors if configured one fails
  if (!priceText) {
    const priceSelectors = [
      '.price', '.product-price', '[data-testid="price"]',
      '.a-price-whole', '.money', '.currency', '.valor'
    ];

    for (const selector of priceSelectors) {
      priceText = $(selector).first().text().trim();
      if (priceText) break;
    }
  }

  // Parse price from text
  if (priceText) {
    // Remove currency symbols and extract numbers
    const priceMatch = priceText.match(/([R\$\$‚Ç¨¬£¬•])\s*([\d.,]+)/i);
    if (priceMatch) {
      currency = priceMatch[1] === 'R$' ? 'BRL' : priceMatch[1];
      currentPrice = parseFloat(priceMatch[2].replace(/[.,]/g, match => match === ',' ? '.' : ''));
    } else {
      // Fallback: extract just numbers
      const numberMatch = priceText.match(/([\d.,]+)/);
      if (numberMatch) {
        currentPrice = parseFloat(numberMatch[1].replace(/[.,]/g, match => match === ',' ? '.' : ''));
      }
    }
  }

  // Extract availability
  const availability = $(config.availability).first().text().trim() || 'unknown';

  // Extract description
  const description = $(config.description).first().text().trim().substring(0, 500);

  // Extract rating
  let rating = 0;
  const ratingText = $(config.rating).first().text().trim();
  if (ratingText) {
    const ratingMatch = ratingText.match(/([\d.]+)/);
    if (ratingMatch) {
      rating = parseFloat(ratingMatch[1]);
    }
  }

  return {
    success: true,
    product_id: productData.product_id,
    product_name: productData.product_name,
    url: productData.product_url,
    title: title,
    current_price: currentPrice,
    original_price: originalPrice || currentPrice,
    currency: currency,
    availability: availability,
    description: description,
    discount_percentage: 0,
    rating: rating,
    extraction_method: 'scrapy_manual',
    tenant_id: productData.tenant_id,
    timestamp: new Date().toISOString()
  };
} else {
  return {
    success: false,
    product_id: productData.product_id,
    url: productData.product_url,
    error: `HTTP ${response.statusCode}: ${response.statusMessage}`,
    extraction_method: 'scrapy_manual',
    tenant_id: productData.tenant_id,
    timestamp: new Date().toISOString()
  };
}

// Calculate price changes and apply markup rules
const currentData = $('Merge Price Results').item.json;
const previousData = $input.all()[0]?.json || {};
const markupRules = $('Extract Parameters').item.json.markup_rules || {};

const previousPrice = previousData.current_price || 0;
const currentPrice = currentData.current_price || 0;

// Calculate price change
const priceChange = currentPrice - previousPrice;
const priceChangePercent = previousPrice > 0 ? ((priceChange / previousPrice) * 100) : 0;

// Apply markup rules based on tenant configuration
const defaultMarkup = markupRules.default_markup_percent || 20;
const categoryMarkup = markupRules.category_markups || {};
const dynamicMarkup = markupRules.dynamic_markup || false;

let markupPercent = defaultMarkup;

// Apply category-specific markup if available
const category = currentData.category || 'general';
if (categoryMarkup[category]) {
  markupPercent = categoryMarkup[category];
}

// Apply dynamic markup based on price trends
if (dynamicMarkup && priceChangePercent < -10) {
  // Increase markup when competitor prices drop significantly
  markupPercent = Math.min(markupPercent * 1.2, 50);
} else if (dynamicMarkup && priceChangePercent > 10) {
  // Decrease markup when competitor prices rise
  markupPercent = Math.max(markupPercent * 0.8, 5);
}

// Calculate suggested selling price
const suggestedPrice = currentPrice * (1 + markupPercent / 100);

// Determine alert conditions
const alertConditions = {
  significant_price_drop: priceChangePercent < -15,
  significant_price_increase: priceChangePercent > 20,
  availability_changed: previousData.availability !== currentData.availability,
  first_time_tracking: !previousData.current_price
};

const alertValues = Object.values(alertConditions);
const shouldAlert = Array.isArray(alertValues) && alertValues.some(condition => condition);

return {
  ...currentData,
  previous_price: previousPrice,
  price_change: priceChange,
  price_change_percent: priceChangePercent,
  markup_percent: markupPercent,
  suggested_selling_price: suggestedPrice,
  alert_conditions: alertConditions,
  should_alert: shouldAlert,
  analysis_timestamp: new Date().toISOString()
};

// Generate alert notification based on price changes
const data = $input.all()[0].json;
const notificationSettings = $('Extract Parameters').item.json.notification_settings || {};

// Build alert message
let alertMessage = `üö® Price Alert: ${data.product_name}\n\n`;

if (data.alert_conditions.significant_price_drop) {
  alertMessage += `üí∞ Significant Price Drop: ${data.price_change_percent.toFixed(2)}%\n`;
}
if (data.alert_conditions.significant_price_increase) {
  alertMessage += `üìà Price Increase: ${data.price_change_percent.toFixed(2)}%\n`;
}
if (data.alert_conditions.availability_changed) {
  alertMessage += `üì¶ Availability Changed: ${data.availability}\n`;
}
if (data.alert_conditions.first_time_tracking) {
  alertMessage += `üÜï Now tracking this product\n`;
}

alertMessage += `\nüíµ Current Price: ${data.currency} ${data.current_price}\n`;
if (data.previous_price > 0) {
  alertMessage += `üìä Previous Price: ${data.currency} ${data.previous_price}\n`;
  alertMessage += `üìâ Change: ${data.currency} ${data.price_change.toFixed(2)}\n`;
}
alertMessage += `üíº Suggested Selling Price: ${data.currency} ${data.suggested_selling_price.toFixed(2)}\n`;
alertMessage += `üîó Product URL: ${data.url}\n\n`;
alertMessage += `‚è∞ ${new Date().toLocaleString()}`;

return {
  alert_message: alertMessage,
  alert_type: 'price_change',
  tenant_id: data.tenant_id,
  product_id: data.product_id,
  notification_channels: notificationSettings.channels || ['email'],
  priority: data.alert_conditions.significant_price_drop ? 'high' : 'normal',
  timestamp: new Date().toISOString()
};
