// Analyze customer message and determine intent and AI provider
const message = $input.first().json;
const businessContext = $node['Get Business Context'].json;

// Extract message details
const messageText = (message.text?.body || '').toLowerCase();
const messageType = message.type || 'text';
const customerPhone = message.from;

// Intent detection logic
function detectIntent(text) {
  const intents = {
    greeting: ['hello', 'hi', 'hey', 'good morning', 'good afternoon', 'ola', 'oi'],
    support: ['help', 'problem', 'issue', 'error', 'not working', 'broken', 'bug'],
    pricing: ['price', 'cost', 'how much', 'pricing', 'payment', 'subscription'],
    product: ['product', 'service', 'feature', 'what do you offer', 'what can you do'],
    technical: ['api', 'integration', 'code', 'documentation', 'technical', 'developer'],
    complaint: ['complaint', 'disappointed', 'angry', 'refund', 'cancel', 'terrible'],
    sales: ['buy', 'purchase', 'order', 'quote', 'demo', 'trial']
  };

  for (const [intent, keywords] of Object.entries(intents)) {
    if (keywords.some(keyword => text.includes(keyword))) {
      return intent;
    }
  }
  return 'general';
}

// Determine best AI provider based on intent and availability
function selectAIProvider(intent) {
  const providerMap = {
    technical: 'mistral',      // Best for technical queries
    complaint: 'cohere',       // Best for sentiment analysis
    pricing: 'gemini',         // Good for business queries
    product: 'gemini',         // Good for product information
    support: 'gemini',         // Best conversational AI
    sales: 'gemini',           // Good for sales conversations
    general: 'gemini'          // Default to Gemini
  };

  return providerMap[intent] || 'gemini';
}

// Detect urgency level
function detectUrgency(text) {
  const urgentKeywords = ['urgent', 'emergency', 'immediately', 'asap', 'critical', 'broken', 'down'];
  const highKeywords = ['important', 'soon', 'quickly', 'problem', 'issue'];

  if (urgentKeywords.some(keyword => text.includes(keyword))) return 'urgent';
  if (highKeywords.some(keyword => text.includes(keyword))) return 'high';
  return 'normal';
}

const detectedIntent = detectIntent(messageText);
const selectedProvider = selectAIProvider(detectedIntent);
const urgencyLevel = detectUrgency(messageText);

// Prepare data for AI processing
return [{
  json: {
    ...message,
    analysis: {
      intent: detectedIntent,
      urgency: urgencyLevel,
      ai_provider: selectedProvider,
      requires_ai: messageText.length > 10 && !['help'].includes(messageText), // Skip AI for simple commands
      conversation_context: {
        customer_phone: customerPhone,
        message_type: messageType,
        timestamp: new Date().toISOString()
      }
    },
    business_context: businessContext
  }
}];

// Multi-provider AI response generation with fallback
const data = $input.first().json;
const businessContext = data.business_context;
const analysis = data.analysis;
const messageText = data.text?.body || '';

// Create comprehensive context for AI
const aiContext = `
You are a customer service AI for ${businessContext.company_info?.name || 'our company'}.

COMPANY CONTEXT:
- Industry: ${businessContext.company_info?.industry || 'Service Provider'}
- Description: ${businessContext.company_info?.description || 'We provide excellent services'}
- Products/Services: ${(businessContext.company_info?.key_products || []).join(', ')}
- Business Hours: ${businessContext.business_policies?.business_hours || '9 AM - 5 PM'}

CONVERSATION STYLE:
- Tone: ${businessContext.conversation_style?.tone || 'professional and friendly'}
- Use emojis: ${businessContext.conversation_style?.use_emojis ? 'Yes' : 'No'}
- Response length: Keep responses ${businessContext.conversation_style?.response_length || 'concise'}

CUSTOMER CONTEXT:
- Intent detected: ${analysis.intent}
- Urgency level: ${analysis.urgency}
- Message type: ${analysis.conversation_context.message_type}

FAQs AND KNOWLEDGE:
${(businessContext.faqs || []).map(faq => `Q: ${faq.question}\nA: ${faq.answer}`).join('\n\n')}

IMPORTANT GUIDELINES:
- Always be helpful and professional
- Provide accurate information based on the company context
- If you don't know something, suggest contacting support
- For urgent matters, provide immediate escalation options
- Keep responses under 500 characters for WhatsApp

Customer message: "${messageText}"

Provide a helpful, context-aware response:`;

// Prepare API calls for different providers
const apiConfigs = {
  gemini: {
    method: 'POST',
    url: 'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent',
    headers: {
      'Content-Type': 'application/json'
    },
    body: {
      contents: [{
        parts: [{ text: aiContext }]
      }],
      generationConfig: {
        temperature: 0.7,
        maxOutputTokens: 200,
        topP: 0.8
      }
    },
    apiKey: process.env.GEMINI_API_KEY
  },
  cohere: {
    method: 'POST',
    url: 'https://api.cohere.ai/v1/generate',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${process.env.COHERE_API_KEY}`
    },
    body: {
      model: 'command',
      prompt: aiContext,
      max_tokens: 200,
      temperature: 0.7
    }
  },
  mistral: {
    method: 'POST',
    url: 'https://api.mistral.ai/v1/chat/completions',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${process.env.MISTRAL_API_KEY}`
    },
    body: {
      model: 'mistral-medium',
      messages: [{ role: 'user', content: aiContext }],
      max_tokens: 200,
      temperature: 0.7
    }
  }
};

// Return configuration for selected AI provider
const selectedConfig = apiConfigs[analysis.ai_provider] || apiConfigs.gemini;

return [{
  json: {
    ...data,
    ai_config: selectedConfig,
    fallback_providers: ['gemini', 'cohere', 'mistral', 'huggingface'].filter(p => p !== analysis.ai_provider)
  }
}];

// Parse AI response and extract the generated text
const aiResponse = $input.first().json;
const originalData = $node['Prepare AI Request'].json;
const analysis = originalData.analysis;

let generatedResponse = '';
let success = false;

try {
  // Parse response based on AI provider
  switch (analysis.ai_provider) {
    case 'gemini':
      if (aiResponse.candidates && aiResponse.candidates[0] && aiResponse.candidates[0].content) {
        generatedResponse = aiResponse.candidates[0].content.parts[0].text;
        success = true;
      }
      break;

    case 'cohere':
      if (aiResponse.generations && aiResponse.generations[0]) {
        generatedResponse = aiResponse.generations[0].text;
        success = true;
      }
      break;

    case 'mistral':
      if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message) {
        generatedResponse = aiResponse.choices[0].message.content;
        success = true;
      }
      break;
  }

  // Clean up response
  if (success && generatedResponse) {
    generatedResponse = generatedResponse.trim();

    // Ensure response isn't too long for WhatsApp
    if (generatedResponse.length > 1000) {
      generatedResponse = generatedResponse.substring(0, 997) + '...';
    }

    // Add appropriate emojis if enabled
    const useEmojis = originalData.business_context.conversation_style?.use_emojis;
    if (useEmojis && !generatedResponse.match(/[\u{1F600}-\u{1F64F}\u{1F300}-\u{1F5FF}\u{1F680}-\u{1F6FF}\u{1F1E0}-\u{1F1FF}]/u)) {
      const emojiMap = {
        greeting: 'üëã',
        support: 'üõ†Ô∏è',
        pricing: 'üí∞',
        product: 'üìã',
        technical: '‚öôÔ∏è',
        sales: 'üíº',
        general: 'üí¨'
      };
      const emoji = emojiMap[analysis.intent] || 'üí¨';
      generatedResponse = `${emoji} ${generatedResponse}`;
    }
  }
} catch (error) {
  console.error('Error parsing AI response:', error);
  success = false;
}

// Fallback to business-context response if AI failed
if (!success || !generatedResponse) {
  const businessContext = originalData.business_context;
  const intent = analysis.intent;

  // Generate contextual fallback responses
  const fallbackResponses = {
    greeting: `Hello! Welcome to ${businessContext.company_info?.name || 'our service'}. How can I help you today?`,
    support: `I'd be happy to help you with support. You can reach our team during ${businessContext.business_policies?.business_hours || 'business hours'}.`,
    pricing: `For pricing information about our services, please contact our sales team or visit our website.`,
    product: `We offer ${(businessContext.company_info?.key_products || ['excellent services']).join(', ')}. What would you like to know more about?`,
    technical: `For technical assistance, our support team can help you during ${businessContext.business_policies?.business_hours || 'business hours'}.`,
    complaint: `I'm sorry to hear about your concern. Let me connect you with our support team to resolve this immediately.`,
    sales: `Thank you for your interest! Our sales team would love to help you. Contact us during ${businessContext.business_policies?.business_hours || 'business hours'}.`,
    general: `Thank you for contacting ${businessContext.company_info?.name || 'us'}. How can I assist you today?`
  };

  generatedResponse = fallbackResponses[intent] || fallbackResponses.general;
}

return [{
  json: {
    ...originalData,
    ai_response: {
      text: generatedResponse,
      provider_used: analysis.ai_provider,
      success: success,
      intent: analysis.intent,
      timestamp: new Date().toISOString()
    }
  }
}];

// AI Provider Fallback System - Try backup providers if primary fails
const originalData = $input.first().json;
const analysis = originalData.analysis;
const errorMessage = originalData.error || 'API request failed';

// Get next available provider from fallback chain
const fallbackProviders = originalData.fallback_providers || ['cohere', 'mistral'];
const nextProvider = fallbackProviders[0];

if (!nextProvider) {
  // No more providers to try, use fallback response
  const businessContext = originalData.business_context;
  const intent = analysis.intent;

  const fallbackResponses = {
    greeting: `Hello! Welcome to ${businessContext.company_info?.name || 'our service'}. How can I help you today? üëã`,
    support: `I'd be happy to help you with support. You can reach our team during ${businessContext.business_policies?.business_hours || 'business hours'}. üõ†Ô∏è`,
    pricing: `For pricing information about our services, please contact our sales team or visit our website. üí∞`,
    product: `We offer ${(businessContext.company_info?.key_products || ['excellent services']).join(', ')}. What would you like to know more about? üìã`,
    technical: `For technical assistance, our support team can help you during ${businessContext.business_policies?.business_hours || 'business hours'}. ‚öôÔ∏è`,
    complaint: `I'm sorry to hear about your concern. Let me connect you with our support team to resolve this immediately. üõ†Ô∏è`,
    sales: `Thank you for your interest! Our sales team would love to help you. Contact us during ${businessContext.business_policies?.business_hours || 'business hours'}. üíº`,
    general: `Thank you for contacting ${businessContext.company_info?.name || 'us'}. How can I assist you today? üí¨`
  };

  return [{
    json: {
      ...originalData,
      ai_response: {
        text: fallbackResponses[intent] || fallbackResponses.general,
        provider_used: 'fallback',
        success: true,
        intent: analysis.intent,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Update analysis to use next provider
const updatedAnalysis = {
  ...analysis,
  ai_provider: nextProvider
};

// Remove used provider from fallback chain
const remainingProviders = fallbackProviders.slice(1);

return [{
  json: {
    ...originalData,
    analysis: updatedAnalysis,
    fallback_providers: remainingProviders,
    retry_attempt: (originalData.retry_attempt || 0) + 1
  }
}];

